import react from 'react';
import React from 'react';
import '/fichaproducto.css'
import {Modal,ModalHeader,ModalBody,ModalFooter, Button, Container} from 'reactstrap';
class FichaProducto extends React.Component{
    constructor(props){
        super();
        this.state={
            modal:false
        };
        this.toggle = this.toggle.bind(this);

    }

    toggle(){
        this.setState(prevState =>({
            modal: !prevState.modal
        }));
        console.log(this.props);

    }
    render(){
      return(
        <Container>
            <Button onClick={this.toggle}>Ver Ficha</Button>
            <Modal isOpen={this.state.modal}>
                <ModalHeader>Modal de React by Dylantero</ModalHeader>
                <ModalBody>
                    Modal de React by Dylantero
                </ModalBody>
                <ModalFooter>
                    <Button onClick={this.state.modal}>Cerrar</Button>
                </ModalFooter>
            </Modal>
        </Container>
      );
    }
}

export default FichaProducto;
