import React from 'react';
import {Modal,Button,Container,ModalBody,Col,Row,Nav,} from 'reactstrap'

class FichaProducto extends React.Component{
    constructor(props){
        super();
        this.state={
            modal:false
        };

        this.toggle = this.toggle.bind(this);
    }
    toggle(){
        this.setState(prevState=>({
            modal: !prevState.modal
        }));
        console.log(this.props);
    }
    render(){
        return(
            <Container>
                <div className="buttondiv">
                 <button className="btnficha" onClick={this.toggle}>Ver Mas</button>
                </div>
                <Modal isOpen={this.state.modal}>
                    <ModalBody>
                        <Container>
                            <Row>
                                <Col lg="12">
                                    <Button close onClick={this.toggle} />
                                </Col>
                                <Col lg="12">
                                    <Container>
                                     <h6>{this.props.props.titulo}</h6>
                                    </Container>
                                </Col>
                            </Row>
                            <br></br>
                            <Row>
                                <Col lg="6">
                                    <Container>
                                        <img alt="imagen-articulo" className="img_descripcion" src={this.props.props.imagen}></img>
                                    </Container>
                                </Col>
                                <Col lg="6">
                                <Container>
                                    <div className="my-2 mx-2">
                                        <span className="text-danger">Valor: </span>
                                        <span className="precioficha text-danger">$ {this.props.props.precio}</span>
                                    </div>
                                    <button className="btnficha" onClick={this.toggle}>Agregar al Carro</button>
                                    <img alt="" src="%PUBLIC_URL%/favicon.ico"></img>
                                </Container>
                                </Col>
                                <Col>
                                <Container>
                                     <small>{this.props.props.descripcion}</small>
                                    </Container>
                                </Col>
                            </Row>
                        </Container>
                    </ModalBody>
                </Modal>
            </Container>
        );
    }
}

export default FichaProducto;
