import React from "react";
import { render } from "react-dom";
import {Button,Col,CardImg,Container,CardSubtitle, Card, Row, CardBody, CardTitle} from "reactstrap";
import "./producto.css";

class Producto extends React.Component{
  render(){
    return(
      <Container>
        <Row>
          <Col lg="4">
            <Card className="Card">
              <CardImg src={this.props.imagen}></CardImg>
              <CardBody>
                <CardTitle>{this.props.titulo}</CardTitle>
                <CardSubtitle>$ {this.props.precio}</CardSubtitle>
                <Button className="Button">Ver Detalles</Button>
                <Button className="Button">Comprar</Button>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Producto;
