import React from 'react';
import { Badge,Button,Popover,PopoverBody,PopoverHeader,Table} from 'reactstrap';

class Carro extends React.Component{

    constructor(){
        super();
        this.state={
            modal:false
        };

        this.toggle = this.toggle.bind(this);
    }
    toggle(){
        this.setState(prevState=>({
            popoverOpen: !prevState.popoverOpen
        }));
    }
    render(){
        return(
            <div>
                <Button id="Popover1" className="btn btn-secondary">
                 <Badge><span className="material-icons">shopping_cart</span>0</Badge>
                </Button>
                
                <Popover target="Popover1" placement="bottom" isOpen={this.state.popoverOpen} toggle={this.toggle}>
                    <PopoverHeader>AÃ±adido al carro</PopoverHeader>
                    <PopoverBody>
                        <Table>
                            <thead>
                                <tr>
                                    <th>Producto</th>
                                    <th>Cantidad</th>
                                    <th>Precio</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Producto</td>
                                    <td>1</td>
                                    <td>$9999</td>
                                </tr>
                            </tbody>
                        </Table>
                    </PopoverBody>
                </Popover>
            </div>
        );
    }
}

export default Carro;
