import React from 'react';
import './producto.css';
import {Modal,Row,Col,ModalHeader,ModalBody,ModalFooter, Button, Container} from 'reactstrap';
class FichaProducto extends React.Component{
    constructor(props){
        super();
        this.state={
            modal:false //Iniciamos el modal en false
        };
        this.toggle = this.toggle.bind(this);//Oculta el objeto

    }

    toggle(){
        this.setState(prevState =>({//Estado del modal
            modal: !prevState.modal
        }));
        console.log(this.props);

    }
    render(){
      return(
        <Container className="text-center">
            <div>
                <Row>  
                  <Col md="12">
                    <Button className="buttonficha btn-sm" onClick={this.toggle}><small className="font-weight-light">VER MAS</small></Button>
                  </Col>
                </Row>
            </div>
            <Modal isOpen={this.state.modal}>
                <ModalBody>
                        <Row>
                            <Col lg="12">
                                <div className="text-right">
                                    <Button type="button" class="close" data-dismiss="modal" onClick={this.toggle} aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </Button>
                                </div>
                            </Col>
                            <Col lg="12">
                                <div className="titulo">
                                    {this.props.props.titulo} 
                                </div>
                            </Col>
                        </Row>
                    Modal de React by Dylantero
                </ModalBody>
                <ModalFooter>
                    
                </ModalFooter>
            </Modal>
        </Container>
      );
    }
}

export default FichaProducto;
