import React from 'react';
import {Modal,Button,Container,ModalBody,Col,Row} from 'reactstrap'

class FichaProducto extends React.Component{
    constructor(props){
        super();
        this.state={
            modal:false
        };

        this.toggle = this.toggle.bind(this);
    }
    toggle(){
        this.setState(prevState=>({
            modal: !prevState.modal
        }));
        console.log(this.props);
    }
    render(){
        return(
            <Container>
                <Button onClick={this.toggle}>Ver ficha</Button>
                <Modal isOpen={this.state.modal}>
                    <ModalBody>
                        <Container>
                            <Row>
                                <Col lg="12">
                                    <Button close onClick={this.toggle} />
                                </Col>
                                <Col lg="12">
                                    <Container>
                                     <h6>{this.props.props.titulo}</h6>
                                    </Container>
                                </Col>
                            </Row>
                            <br></br>
                            <Row>
                                <Col lg="6">
                                    <Container>
                                        <img alt="imagen-articulo" className="img_descripcion" src={this.props.props.imagen}></img>
                                    </Container>
                                </Col>
                                <Col lg="6">
                                    <Container>
                                     <p>{this.props.props.descripcion}</p>
                                    </Container>
                                </Col>
                            </Row>
                        </Container>
                        <Container>
                            <Button onClick={this.toggle}>Agregar al carrito</Button>
                        </Container>
                    </ModalBody>
                </Modal>
            </Container>
        );
    }
}

export default FichaProducto;
