import React from "react";
import {Button,Col,CardImg,Container,CardSubtitle, Card, Row, CardBody, CardTitle} from "reactstrap";
import "./producto.css";

function Producto(props){
  return(
    <div className="Container">
      <Col lg="3">
        <Card className="Card">
          <CardImg><img>{props.imagen}</img></CardImg>
          <CardBody>
            <CardTitle>{props.titulo}</CardTitle>
            <CardSubtitle>$ {props.precio}</CardSubtitle>
            <Button className="Button">Ver Detalles</Button>
            <Button className="Button">Comprar</Button>
          </CardBody>
        </Card>
      </Col>
    </div>
  );
}

export default Producto;
