import React from "react";
//import { render } from "react-dom";
import {Col,CardImg,CardSubtitle, Card, CardBody, CardTitle} from "reactstrap";
import "./producto.css";
import FichaProducto from './fichaproducto';

class Producto extends React.Component{
  render(){
    return(
        <Col lg="3" md="4" sm="6" xs="6">
            <div>
              <Card className="Card">
                <CardTitle className="text-center">{this.props.titulo}</CardTitle>
                <CardSubtitle className="text-center font-weight-light text-muted py-1">{this.props.marca}</CardSubtitle>
                <CardImg className="cajaobjetos" src={this.props.imagen}></CardImg>
                <CardBody>
                  <CardSubtitle className="text-danger text-center font-weight-light">Precio $<span className="font-weight-normal">{this.props.precio}</span></CardSubtitle>
                  <FichaProducto props={this.props}></FichaProducto>
                </CardBody>
              </Card>
            </div>
        </Col>
    );
  }
}

export default Producto;
