import React from 'react';
import {Container,Col,Row,Button} from 'reactstrap';
import './producto.css';

class Footer extends React.Component{
    render(){
        return(
            <footer className="bg-dark">
                <Container>
                   <Row>
                      <Col lg="3" md="6" sm="6" xs="12">
                         <div>
                             <Button>Contactanos</Button>
                         </div> 
                      </Col>  
                      <Col lg="3" md="6" sm="6" xs="12">
                        <div>
                            <h3 class="">SERVICIO AL CLIENTE</h3>

                            <ul class="">
                                <li><a href="/"><span>•</span>Términos y Condiciones</a></li>
                                <li><a href="/"><span>•</span>Privacidad y Seguridad</a></li>
                                <li><a href="/"><span>•</span>Orden de Servicio</a></li>
                                <li><a href="/"><span>•</span>Términos y Condiciones Servicio de Garantía Daño y Robo</a></li>
                                
                            </ul>
                        </div>    
                      </Col>  
                      <Col lg="3" md="6" sm="6" xs="12"></Col>  
                      <Col lg="3" md="6" sm="6" xs="12"></Col>  
                   </Row>
                </Container>
            </footer>
        );
    }
}

export default Footer;
