import React from "react";
//import { render } from "react-dom";
import {Col,CardImg,CardSubtitle, Card, CardBody, CardTitle} from "reactstrap";
import "./producto.css";
import FichaProducto from './fichaproducto';

class Producto extends React.Component{
  render(){
    return(
        <Col md="3">
            <Card className="Card">
              <CardTitle className="text-center">{this.props.titulo}</CardTitle>
              <CardSubtitle className="text-center">{this.props.marca}</CardSubtitle>
              <CardImg className="img img-responsive" src={this.props.imagen}></CardImg>
              <CardBody>
                <CardSubtitle className="text-danger">Precio $<span>{this.props.precio}</span></CardSubtitle>
                <FichaProducto props={this.props}></FichaProducto>
              </CardBody>
            </Card>
          </Col>
    );
  }
}

export default Producto;
