import React from 'react';
import './producto.css';
import {Modal,Row,Col,ModalHeader,ModalBody,ModalFooter, Button, Container} from 'reactstrap';
class FichaProducto extends React.Component{
    constructor(props){
        super();
        this.state={
            modal:false //Iniciamos el modal en false
        };
        this.toggle = this.toggle.bind(this);//Oculta el objeto

    }

    toggle(){
        this.setState(prevState =>({//Estado del modal
            modal: !prevState.modal
        }));
        console.log(this.props);

    }
    render(){
      return(
        <Container className="text-center">
            <div>
                <Row>
                  <Col md="8">
                    <Button className="buttonficha" onClick={this.toggle}><small className="font-weight-light">Agregar al carrito</small></Button>
                  </Col>  
                  <Col  md="4">
                    <Button className="buttonficha" onClick={this.toggle}>Ver Mas</Button>
                  </Col>
                </Row>
            </div>
            <Modal isOpen={this.state.modal}>
                <ModalHeader> {this.props.props.titulo}  </ModalHeader>
                <ModalBody>
                    Modal de React by Dylantero
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={this.toggle}>Cerrar</Button>
                </ModalFooter>
            </Modal>
        </Container>
      );
    }
}

export default FichaProducto;
